import { getRlpHexOrError } from "../../../routes/validators/utils";

describe("getRlpHexOrError", () => {
  it("should return decoded array of transaction hashes", () => {
    expect(
      getRlpHexOrError(
        "",
      ),
    ).toEqual([
      "0xfc2b3b6db38a51db3b9cb95de29b719de8deb99630626e4b4b99df056ffb7f2e",
      "0x48603f7adff7fbfc2a10b22a6710331ee68f2e4d1cd73a584d57c8821df79356",
      "0xcbc920e7bb89cbcb540a469a16226bf1057825283ab8eac3f45d00811eef8a64",
      "0x6d604ffc644a282fca8cb8e778e1e3f8245d8bd1d49326e3016a3c878ba0cbbd",
    ]);
  });

  it("should return an array of transaction hashes if the rlpHex is an array after decoding", () => {
    expect(
      getRlpHexOrError(
        "0xf842a0fc2b3b6db38a51db3b9cb95de29b719de8deb99630626e4b4b99df056ffb7f2ea048603f7adff7fbfc2a10b22a6710331ee68f2e4d1cd73a584d57c8821df79356",
      ),
    ).toEqual([
      "0xfc2b3b6db38a51db3b9cb95de29b719de8deb99630626e4b4b99df056ffb7f2e",
      "0x48603f7adff7fbfc2a10b22a6710331ee68f2e4d1cd73a584d57c8821df79356",
    ]);
  });

  it("should return a single transaction id if the rlpHex is not array after decoplanding", () => {
    expect(
      getRlpHexOrError(
        "0xa0fc2b3b6db38a51db3b9cb95de29b719de8deb99630626e4b4b99df056ffb7f2e",
      ),
    ).toEqual(
      "0xfc2b3b6db38a51db3b9cb95de29b719de8deb99630626e4b4b99df056ffb7f2e",
    );
  });
});
